// App.js
import React, { useState } from 'react';

const App = () => {
    const [query, setQuery] = useState('');
    const [method, setMethod] = useState('lexical');
    const [vector, setVector] = useState(''); // 벡터 입력 추가
    const [results, setResults] = useState([]);
    const [totalCount, setTotalCount] = useState(0);

    const handleSearch = async () => {
        try {
            const requestBody = { query, method };
            if (method !== 'lexical') {
                requestBody.vector = vector.split(',').map(Number); // CSV 형식 입력 -> 숫자 배열 변환
            }

            const response = await fetch('http://localhost:3000/search', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(requestBody),
            });

            const data = await response.json();
            setResults(data.results);
            setTotalCount(data.totalCount);
        } catch (error) {
            console.error('Error fetching search results:', error);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Movie Search</h1>
            <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Enter search query"
                style={{ width: '300px', marginRight: '10px' }}
            />
            {method !== 'lexical' && (
                <input
                    type="text"
                    value={vector}
                    onChange={(e) => setVector(e.target.value)}
                    placeholder="Enter embedding vector (comma-separated)"
                    style={{ width: '400px', marginRight: '10px' }}
                />
            )}
            <div>
                <label>
                    <input
                        type="radio"
                        value="lexical"
                        checked={method === 'lexical'}
                        onChange={(e) => setMethod(e.target.value)}
                    />
                    Lexical
                </label>
                <label>
                    <input
                        type="radio"
                        value="hybrid"
                        checked={method === 'hybrid'}
                        onChange={(e) => setMethod(e.target.value)}
                    />
                    Hybrid
                </label>
                <label>
                    <input
                        type="radio"
                        value="rag"
                        checked={method === 'rag'}
                        onChange={(e) => setMethod(e.target.value)}
                    />
                    RAG
                </label>
            </div>
            <button onClick={handleSearch} style={{ marginTop: '10px' }}>
                Search
            </button>
            <div style={{ marginTop: '20px' }}>
                <h2>Total Results: {totalCount}</h2>
                <ul>
                    {results.map((result, index) => (
                        <li key={index}>
                            <strong>{result.title}</strong>: {result.plot}
                            {result.summary && <p>{result.summary}</p>}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default App;
