import React, { useState } from "react";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [mode, setMode] = useState("lexical");
  const [results, setResults] = useState([]);
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    setLoading(true);
    setError("");
    setResults([]);
    setAnswer("");

    try {
      const response = await fetch("http://localhost:3000/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query, mode }),
      });

      const data = await response.json();

      if (response.ok) {
        setResults(data.results);
        if (mode === "rag") setAnswer(data.answer || "No answer provided.");
      } else {
        setError(data.error);
      }
    } catch {
      setError("Failed to connect to the server.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Movie Search</h1>
        <div className="container">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Enter your query..."
            className="search-box"
          />
          <select value={mode} onChange={(e) => setMode(e.target.value)} className="select-box">
            <option value="lexical">Lexical</option>
            <option value="hybrid">Hybrid</option>
            <option value="rag">RAG</option>
          </select>
          <button onClick={handleSearch} disabled={loading} className="select-box">
            {loading ? "Searching..." : "Search"}
          </button>
        </div>
        {error && <p className="error">{error}</p>}
        {mode === "rag" && answer && (
          <div>
            <h2>Generated Answer</h2>
            <p>{answer}</p>
          </div>
        )}
        {results.length > 0 && (
            <table className="results-table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Plot</th>
                  <th>Full Plot</th>
                  <th>Similarity / Score</th>
                </tr>
              </thead>
              <tbody>
                {results.map((result) => (
                  <tr key={result._id}>
                    <td>{result.title}</td>
                    <td>{result.plot || "N/A"}</td>
                    <td>{result.fullplot || "N/A"}</td>
                    <td>
                      {result.similarity !== undefined
                        ? result.similarity.toFixed(4)
                        : result.score !== undefined
                        ? result.score.toFixed(4)
                        : "N/A"}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}

      </header>
    </div>
  );
}

export default App;
